package mpt

import (
	"encoding/json"
	"testing"

	"github.com/joeqian10/neo3-gogogo/helper"
	"github.com/joeqian10/neo3-gogogo/helper/io"
)

func TestStateRootSerialize(t *testing.T) {
	var sr StateRoot
	data := []byte(`{"version":0,"index":409,"roothash":"0x5094cbeb0642557262cb3ea61d57a1b47119eafce9725ec4ac79398c30b3c7a0","witness":{"invocation":"","verification":"5321030f59a5482a4e42a2e5a848608dac4e84a698e567e2860e0ca5f23fc9e818d37c21032e78261370d4d62cf4c13584ca90f46c5565117b5b97544312f2e7b7c36b9eba21026e271722c21c482f0ac74dd932e61cdc2a2dd889633a2c5d8ecef43f2769f51e2103d55bfbcd493d06ab49c09cde0cea5d9ba890d81331a2fcd6f68d329932d0398f54ae"}}`)
	err := json.Unmarshal(data, &sr)
	if err != nil {
		t.Error(err)
	}
	data, _ = io.ToArray(&sr)
	if helper.BytesToHex(data) != "" {
		t.Error("serialize failed")
	}
	io.AsSerializable(&sr, data)
	data, _ = io.ToArray(&sr)
	if helper.BytesToHex(data) != "" {
		t.Error("serialize failed")
	}
}
